import React, { useState,Component } from 'react'


import {Button,ButtonToolbar} from 'react-bootstrap';

export class NewMultiForm extends Component{

    constructor(props){
        super(props);
        this.state={std:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'Student')
        .then(response=>response.json())
        .then(data=>{
            this.setState({stds:data})
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }

    
    
      handleChangeInput(i, e){
        const [fields, setFields] = useState([{
            StudentID: 1,
            StudentFirstName: "",
            StudentLastName: "",
            StudentPhoto:"",
            StudentNote:""
          }])
        console.log(e.target.value);
        const values = [...fields]
        values[i][e.target.name] = e.target.value
        setFields(values)
      }
    
    
      handleAdd(StudentID){
        const [fields, setFields] = useState([{
            StudentID: 1,
            StudentFirstName: "",
            StudentLastName: "",
            StudentPhoto:"",
            StudentNote:""
          }])
        setFields([...fields, { StudentID: StudentID + 2, StudentFirstName: '', StudentLastName: '',StudentPhoto:'',StudentNote:'' }])
      }
    
      handleSubtract(i){
        const [fields, setFields] = useState([{
            StudentID: 1,
            StudentFirstName: "",
            StudentLastName: "",
            StudentPhoto:"",
            StudentNote:""
          }])
        const values = [...fields]
        values.splice(i, 1)
        setFields([...values])
      }

    // deleteStd(stdid){
    //     if(window.confirm('Are you sure?')){
    //         fetch(process.env.REACT_APP_API+'Student/'+stdid,{
    //             method:'DELETE',
    //             header:{'Accept':'application/json',
    //         'Content-Type':'application/json'}
    //         })
    //     }
    // }

    render(){
        const {stds, stdid, stdfirst, stdlast, stdphoto, stdnote}=this.state;
        let addModalClose=()=> this.setState({addModalShow:false});
        let editModalClose=()=> this.setState({editModalShow:false});
        return(    
            <div>
                <Container>
                    <Row>
                    <Form> 
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                        {fields.map((field, i) => (
                            <div key={field.StudentID}>
                            <Row className="mt-5">
                                <Col md>
                                <Form.Label>First Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    required
                                    placeholder="Enter First Name"
                                    name="StudentFirstName"
                                    value={field.StudentFirstName}
                                    onChange={e => handleChangeInput(i, e)}
                                />
                                </Col>
                                <Col md>
                                <Form.Label>Last Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    required
                                    placeholder="Enter Last Name"
                                    name="StudentLastName"
                                    value={field.StudentLastName}
                                    onChange={e => handleChangeInput(i, e)}
                                />
                                </Col>
                                <Col md>
                                <Form.Label>Optional Note</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Note"
                                    name="StudentNote"
                                    value={field.StudentNote}
                                    onChange={e => handleChangeInput(i, e)}
                                />
                                </Col>
                                {/* <Col md>
                                    <Image wStudentIDth="200px" height="200px" src={this.imagesrc}/>
                                    <input onChange={this.handleFileSelected} type="File"/>
                                </Col> */}
                                <Col md>
                                <Button onClick={() => handleAdd(i)} className="mt-4 mr-5" variant="success">
                                    <i className="fas fa-plus"></i>
                                    + 1
                                </Button>
                                <Button disabled={field.StudentID === 1} onClick={() => handleSubtract(i)} className="mt-4" variant="danger">
                                    <i className="fas fa-minus"></i>
                                    Del
                                </Button>
                                </Col>
                            </Row>
                            </div>
                        ))}
                        </Form.Group>
                        <Button type="submit" variant="dark" style={{ float: "left" }} >
                        Send to Database
                        </Button>
                    </Form>

                    {fields.map(field =>
                        <>
                        <div className="m-5">
                            <div>{field.StudentID}</div>
                            <div>{field.StudentFirstName}</div>
                            <div>{field.StudentLastName}</div>
                            <div>{field.StudentNote}</div>
                        </div>
                        </>
                    )}
                    </Row>
                </Container>
    </div>
        )
        }
}
