import React, {Component} from 'react';
import{Modal, Button, Row, Col, Form} from 'react-bootstrap';

export class AddStdModal extends Component{
    constructor(props){
        super(props);
        this.handleSubmit=this.handleSubmit.bind(this);
    }

    handleSubmit(event){
        event.preventDefault();
        fetch(process.env.REACT_APP_API+'Student',{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                StudentID:null,
                StudentFirstName:event.target.StudentFirstName,
                // StudentLastName:event.target.StudentLastName.value,
                // StudentPhoto:event.target.StudentPhoto.value,
                // StudentNote:event.target.StudentNote.value
            })
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
        },
        (error)=>{
            alert('Failed');
        })
    }
    render(){
        return(
            <div className="container">
                <Modal {...this.props}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
                >
                    <Modal.Header clooseButton>
                        <Modal.Title id="contained-modal-title-vcenter">
                            Add Student
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Row>
                            <Col sm={6}> 
                                <Form onSubmit={this.handleSubmit}>
                                    
                                    <Form.Group controlId="StudentFirstName">
                                        <Form.Label>First Name</Form.Label>
                                        <Form.Control type="text" name="StudentFirstName" required placeholder="Enter First Name"/>
                                    </Form.Group>

                                    {/* <Form.Group controlId="StudentLastName">
                                        <Form.Label>Last Name</Form.Label>
                                        <Form.Control type="text" name="StudentLastName" required placeholder="Enter Last Name"/>
                                    </Form.Group>

                                    <Form.Group controlId="StudentPhoto">
                                        <Form.Label>Insert Photo</Form.Label>
                                        <Form.Control type="text" name="StudentPhoto" required placeholder="Submit photo"/>
                                    </Form.Group>

                                    <Form.Group controlId="StudentNote">
                                        <Form.Label>Additional Notes</Form.Label>
                                        <Form.Control type="text" name="StudentNote" placeholder="Note"/>
                                    </Form.Group> */}
                                    

                                    <Form.Group>
                                        <Button variant="primary" type="submit">
                                            Register Student
                                        </Button>
                                    </Form.Group>
                                </Form>
                            </Col>
                        </Row>
                        
                    </Modal.Body>

                    <Modal.Footer>
                        <Button variant="danger" onClick={this.props.onHide}>Close</Button>
                    </Modal.Footer>

                </Modal>
            </div>
        )
    }
}
