import React, {Component} from 'react';
import{Button, Row, Col, Form} from 'react-bootstrap';
// import { Form, Col } from 'react-bootstrap'
// import Row from "react-bootstrap/row"
import Container from "react-bootstrap/container"
// import Button from "react-bootstrap/Button"

export class FormMulti extends Component{
    constructor(props){
        super(props);
        this.handleSubmit=this.handleSubmit.bind(this);
        this.handleFileSelected=this.handleFileSelected.bind(this);
        this.handleChangeInput=this.handleChangeInput.bind(this);
        this.handleAdd=this.handleAdd.bind(this);
        this.handleSubtract=this.handleSubtract(this);
    }

    stdphoto = "anonymous.png";
    imagesrc = process.env.REACT_APP_PHOTOPATH+this.stdphoto;

    handleSubmit(event){
        event.preventDefault();
        fetch(process.env.REACT_APP_API+'Student',{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                StudentID:null,
                StudentFirstName:event.target.StudentFirstName.value,
                StudentLastName:event.target.StudentLastName.value,
                StudentPhoto:this.stdphoto,
                StudentNote:event.target.StudentNote.value,
            })
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
        },
        (error)=>{
            alert('Failed');
        })
    }

    handleFileSelected(event){
        event.preventDefault();
        this.stdphoto=event.target.files[0].name;
        const formData = new FormData();
        formData.append(
            "myFile",
            event.target.files[0],
            event.target.files[0].name
        );

        fetch(process.env.REACT_APP_API+'Student/SaveFile',{
            method:'POST',
            body:formData
        })
        .then(res=>res.json())
        .then((result)=>{
            this.imagesrc=process.env.REACT_APP_PHOTOPATH+result;
        },
        (error)=>{
            alert('Failed');
        })
        
    }

    // const [fields, setFields] = useState([{
    //     StudentID: 1,
    //     StudentFirstName: "",
    //     StudentLastName: "",
    //     StudentPhoto:"",
    //     StudentNote:""
    //   }])
    
    handleChangeInput (i, event) {
    console.log(event.target.value);
    const values = [...fields]
    values[i][event.target.name] = event.target.value
    setFields(values)
    }
    
    handleAdd (StudentID) {
    setFields([...fields, { StudentID: StudentID + 2, StudentFirstName: '', StudentLastName: '',StudentPhoto:'',StudentNote:'' }])
    }
    
    handleSubtract(i) {
    const values = [...fields]
    values.splice(i, 1)
    setFields([...values])
    }

    render(){
        return(
            <div>
                <Container>
                    <Row>
                    <Form > 
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                        {fields.map((field, i) => (
                            <div key={field.StudentID}>
                            <Row className="mt-5">
                                <Col md>
                                <Form.Label>First Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    required
                                    placeholder="Enter First Name"
                                    name="StudentFirstName"
                                    value={field.StudentFirstName}
                                    onChange={e => handleChangeInput(i, e)}
                                />
                                </Col>
                                <Col md>
                                <Form.Label>Last Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    required
                                    placeholder="Enter Last Name"
                                    name="StudentLastName"
                                    value={field.StudentLastName}
                                    onChange={e => handleChangeInput(i, e)}
                                />
                                </Col>
                                <Col md>
                                <Form.Label>Optional Note</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Note"
                                    name="StudentNote"
                                    value={field.StudentNote}
                                    onChange={e => handleChangeInput(i, e)}
                                />
                                </Col>
                                {/* <Col md>
                                    <Image wStudentIDth="200px" height="200px" src={this.imagesrc}/>
                                    <input onChange={this.handleFileSelected} type="File"/>
                                </Col> */}
                                <Col md>
                                <Button onClick={() => handleAdd(i)} className="mt-4 mr-5" variant="success">
                                    <i className="fas fa-plus"></i>
                                    + 1
                                </Button>
                                <Button disabled={field.StudentID === 1} onClick={() => handleSubtract(i)} className="mt-4" variant="danger">
                                    <i className="fas fa-minus"></i>
                                    Del
                                </Button>
                                </Col>
                            </Row>
                            </div>
                        ))}
                        </Form.Group>
                        <Button type="submit" onClick={() => handleSubmit()} variant="dark" style={{ float: "left" }} >
                        Send to Database
                        </Button>
                    </Form>

                    {fields.map(field =>
                        <>
                        <div className="m-5">
                            <div>{field.StudentID}</div>
                            <div>{field.StudentFirstName}</div>
                            <div>{field.StudentLastName}</div>
                            <div>{field.StudentNote}</div>
                        </div>
                        </>
                    )}
                    </Row>
                </Container>
            </div>
        )
    }
}
