import React,{Component} from 'react';

import {Table} from 'react-bootstrap';

import {Button,ButtonToolbar} from 'react-bootstrap';


export class Student extends Component{

    constructor(props){
        super(props);
        this.state={std:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'student')
        .then(response=>response.json())
        .then(data=>{
            this.setState({stds:data})
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }

    render(){
        const {stds}=this.state;
        return(    
            <div>
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>StudentID</th>
                        <th>StudentFirstName</th>
                        {/* <th>StudentLastName</th>
                        <th>StudentPhoto</th>
                        <th>StudentNote</th> */}
                        <th>Options</th>
                        </tr>

                    </thead>
                    <tbody>
                        {stds?.map(std=>
                            <tr key={std.StudentID}>
                                <td>{std.StudentID}</td>
                                <td>{std.StudenFirstName}</td>
                                {/* <td>{std.StudenLastName}</td>
                                <td>{std.StudentPhoto}</td>
                                <td>{std.StudentNote}</td> */}
                                <td>Edit / Delete</td>
                            </tr>)}
                    </tbody>
                </Table>
            </div>
        )
        }
}
