import React,{Component} from 'react';

export class Student extends Component{

    constructor(props){
        super(props);
        this.state={deps:[], addModalShow:false, editModalShow:false}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'Student')
        .then(response=>response.json())
        .then(data=>{
            this.setState({stds:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }


    deleteStd(stdid){
        if(window.confirm('Are you sure?')){
            fetch(process.env.REACT_APP_API+'Student/'+stdid,{
                method:'DELETE',
                header:{'Accept':'application/json',
            'Content-Type':'application/json'}
            })
        }
    }


    render(){
        const {stds, stdid,StudentFirstName,StudentLasttName,photofilename,StudentNote}=this.state;
        let addModalClose=()=>this.setState({addModalShow:false});
        let editModalClose=()=>this.setState({editModalShow:false});
        return(
            <div >
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>StudentID</th>
                        <th>StudentFirstName</th>
                        <th>StudentLastName</th>
                        <th>PhotoFileName</th>
                        <th>StudentNote</th>
                        <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stds.map(std=>
                            <tr key={std.StudentID}>
                                <td>{std.StudentFirstName}</td>
                                <td>{std.StudentLastName}</td>
                                <td>{std.PhotoFileName}</td>
                                <td>{std.StudentNote}</td>
                                <td>
<ButtonToolbar>
    <Button className="mr-2" variant="info"
    onClick={()=>this.setState({editModalShow:true,
        stdid:std.StudentID,StudentFirstName:std.StudentFirstName,StudentLastName:std.StudentLastName,
        photofilename:std.PhotoFileName,StudentNote:std.StudentNote})}>
            Edit
        </Button>

        <Button className="mr-2" variant="danger"
    onClick={()=>this.deleteStd(std.StudentID)}>
            Delete
        </Button>

        <EditStdModal show={this.state.editModalShow}
        onHide={editModalClose}
        stdid={empid}
        empnamStudentFirstNamee={empname}
        depmt={depmt}
        photofilename={photofilename}
        doj={doj}
        />
</ButtonToolbar>

                                </td>

                            </tr>)}
                    </tbody>

                </Table>

                <ButtonToolbar>
                    <Button variant='primary'
                    onClick={()=>this.setState({addModalShow:true})}>
                    Add Employee</Button>

                    <AddEmpModal show={this.state.addModalShow}
                    onHide={addModalClose}/>
                </ButtonToolbar>
            </div>
        )
    }
}
